query:
fragment TokenFields on Token {
  id
  name
  symbol
  derivedETH
  tradeVolume
  tradeVolumeUSD
  untrackedVolumeUSD
  totalLiquidity
  txCount
  __typename
}

query tokens {
  tokens(block: {number: 980288}, where: {id: "0xc00b1ffa922edf1175a4e6feaac5b2b469932524"}) {
    ...TokenFields
    __typename
  }
  pairs0: pairs(where: {token0: "0xc00b1ffa922edf1175a4e6feaac5b2b469932524"}, first: 50, orderBy: reserveUSD, orderDirection: desc) {
    id
    __typename
  }
  pairs1: pairs(where: {token1: "0xc00b1ffa922edf1175a4e6feaac5b2b469932524"}, first: 50, orderBy: reserveUSD, orderDirection: desc) {
    id
    __typename
  }
}
